    45→            <div class="facet-list">
    46→              <div
    47→                v-for="(option, index) in filteredOptions"
    48→                :key="option.value"
    49→                :draggable="true"
    50→                :class="[
    51→                  'facet-item',
    52→                  {
    53→                    selected: option.selected,
    54→                    dragging: draggedItem === option.value,
    55→                  },
    56→                ]"
    57→                @dragstart="handleDragStart($event, option)"
    58→                @dragover="handleDragOver($event, index)"
    59→                @drop="handleDrop($event, index)"
    60→                @dragend="handleDragEnd"
    61→              >
