    63→                  <Checkbox
    64→                    :model-value="option.selected"
    65→                    @update:model-value="toggleSelection(option.value)"
    66→                    :binary="true"
    67→                    class="facet-checkbox"
    68→                  />
    69→                  <span class="facet-label">{{ option.label }}</span>
    70→                  <i
    71→                    class="pi pi-bars facet-drag-handle"
    72→                    title="Drag to reorder"
    73→                  />
    74→                </div>
    75→                <div
    76→                  v-if="dragOverIndex === index && draggedItem !== option.value"
    77→                  :class="['drop-indicator', dragPosition]"
    78→                />
    79→              </div>
    80→            </div>
    81→          </div>
    82→        </div>
    83→      </Transition>
    84→    </Teleport>
    85→  </div>
