    88→<script setup lang="ts">
    89→import { ref, computed, onMounted, onUnmounted, watch } from 'vue';
    90→import Button from 'primevue/button';
    91→import Checkbox from 'primevue/checkbox';
    92→import InputText from 'primevue/inputtext';
    93→
    94→interface FacetOption {
    95→  label: string;
    96→  value: string;
    97→  selected: boolean;
    98→}
    99→
   100→const props = defineProps<{
   101→  options: { label: string; value: string }[];
   102→  modelValue: string[];
   103→}>();
   104→
   105→const emit = defineEmits<{
   106→  (e: 'update:modelValue', value: string[]): void;
   107→}>();
   108→
   109→const isOpen = ref(false);
   110→const searchQuery = ref('');
   111→const triggerButton = ref<HTMLElement>();
   112→const dropdownRef = ref<HTMLElement>();
   113→
   114→const draggedItem = ref<string | null>(null);
   115→const dragOverIndex = ref<number>(-1);
   116→const dragPosition = ref<'above' | 'below'>('below');
   117→
   118→const internalOptions = ref<FacetOption[]>([]);
   119→
   120→const initializeOptions = () => {
   121→  internalOptions.value = props.options.map(option => ({
   122→    ...option,
   123→    selected: props.modelValue.includes(option.value),
   124→  }));
   125→};
   126→
   127→watch(
   128→  () => props.modelValue,
   129→  () => {
   130→    internalOptions.value = internalOptions.value.map(option => ({
   131→      ...option,
   132→      selected: props.modelValue.includes(option.value),
   133→    }));
   134→  },
   135→  { deep: true },
   136→);
   137→
   138→watch(() => props.options, initializeOptions, { deep: true });
   139→
   140→onMounted(initializeOptions);
   141→
   142→const filteredOptions = computed(() => {
   143→  if (!searchQuery.value) return internalOptions.value;
   144→  const query = searchQuery.value.toLowerCase();
   145→  return internalOptions.value.filter(option =>
   146→    option.label.toLowerCase().includes(query),
   147→  );
   148→});
   149→
   150→// Computed values for UI
   151→const selectedCount = computed(
   152→  () => internalOptions.value.filter(option => option.selected).length,
   153→);
   154→const totalCount = computed(() => props.options.length);
   155→const selectAllState = computed(() => {
   156→  const selected = selectedCount.value;
   157→  const total = internalOptions.value.length;
   158→  return selected === total;
   159→});
   160→const isIndeterminate = computed(() => {
   161→  const selected = selectedCount.value;
   162→  return selected > 0 && selected < internalOptions.value.length;
   163→});
   164→
   165→const dropdownStyle = ref<Record<string, string>>({});
   166→
   167→const calculatePosition = () => {
   168→  if (!triggerButton.value) return;
   169→
   170→  const rect = triggerButton.value.getBoundingClientRect();
   171→  const dropdownWidth = 350;
   172→
   173→  const top = rect.bottom + 4;
   174→  let left = rect.right - dropdownWidth;
   175→
   176→  // Adjust if off-screen
   177→  if (left < 8) left = rect.left;
   178→  if (left + dropdownWidth > window.innerWidth - 8) {
   179→    left = window.innerWidth - dropdownWidth - 8;
   180→  }
   181→
   182→  dropdownStyle.value = {
   183→    position: 'fixed',
   184→    top: `${Math.max(8, top)}px`,
   185→    left: `${left}px`,
   186→    width: `${dropdownWidth}px`,
   187→    zIndex: '9999',
   188→  };
   189→};
   190→
   191→// Event handlers
   192→const emitUpdate = () => {
   193→  const selected = internalOptions.value
   194→    .filter(option => option.selected)
   195→    .map(option => option.value);
   196→  emit('update:modelValue', selected);
   197→};
   198→
   199→const toggleDropdown = () => {
   200→  isOpen.value = !isOpen.value;
   201→  if (isOpen.value) {
   202→    searchQuery.value = '';
   203→    calculatePosition();
   204→  }
   205→};
   206→
   207→const toggleSelection = (value: string) => {
   208→  const current = props.modelValue;
   209→  const newValue = current.includes(value)
   210→    ? current.filter(v => v !== value)
   211→    : [...current, value];
   212→  emit('update:modelValue', newValue);
   213→};
   214→
   215→const handleSelectAllChange = (checked: boolean) => {
   216→  const newValue = checked ? internalOptions.value.map(opt => opt.value) : [];
   217→  emit('update:modelValue', newValue);
   218→};
   219→
   220→// Simplified drag and drop
   221→const handleDragStart = (event: DragEvent, option: FacetOption) => {
   222→  draggedItem.value = option.value;
   223→  const { dataTransfer } = event;
   224→  if (dataTransfer) {
   225→    dataTransfer.effectAllowed = 'move';
   226→  }
   227→};
   228→
   229→const handleDragOver = (event: DragEvent, index: number) => {
   230→  event.preventDefault();
   231→  const { dataTransfer } = event;
   232→  if (dataTransfer) {
   233→    dataTransfer.dropEffect = 'move';
   234→  }
   235→
   236→  dragOverIndex.value = index;
   237→  const rect = (event.currentTarget as HTMLElement).getBoundingClientRect();
   238→  const y = event.clientY - rect.top;
   239→  dragPosition.value = y < rect.height / 2 ? 'above' : 'below';
   240→};
   241→
   242→const handleDrop = (event: DragEvent, dropIndex: number) => {
   243→  event.preventDefault();
   244→
   245→  if (!draggedItem.value) return;
   246→
   247→  const draggedIndex = internalOptions.value.findIndex(
   248→    option => option.value === draggedItem.value,
   249→  );
   250→
   251→  if (draggedIndex === -1) return;
   252→
   253→  // Calculate target position
   254→  let targetIndex = dropIndex;
   255→  if (dragPosition.value === 'below') targetIndex++;
   256→  if (draggedIndex < targetIndex) targetIndex--;
   257→
   258→  if (draggedIndex === targetIndex) return;
   259→
   260→  // Reorder internal options
   261→  const [draggedOption] = internalOptions.value.splice(draggedIndex, 1);
   262→  internalOptions.value.splice(targetIndex, 0, draggedOption);
   263→
   264→  emitUpdate();
   265→
   266→  // Reset drag state
   267→  draggedItem.value = null;
   268→  dragOverIndex.value = -1;
   269→};
   270→
   271→const handleDragEnd = () => {
   272→  draggedItem.value = null;
   273→  dragOverIndex.value = -1;
   274→};
   275→
   276→// Outside click handler
   277→const handleClickOutside = (event: Event) => {
   278→  if (
   279→    isOpen.value &&
   280→    triggerButton.value &&
   281→    dropdownRef.value &&
   282→    !triggerButton.value.contains(event.target as Node) &&
   283→    !dropdownRef.value.contains(event.target as Node)
   284→  ) {
   285→    isOpen.value = false;
   286→  }
   287→};
   288→
   289→// Lifecycle
   290→onMounted(() => {
   291→  document.addEventListener('click', handleClickOutside);
   292→  window.addEventListener('resize', calculatePosition);
   293→});
   294→
   295→onUnmounted(() => {
   296→  document.removeEventListener('click', handleClickOutside);
   297→  window.removeEventListener('resize', calculatePosition);
   298→});
   299→
   300→// Watch for dropdown position updates
   301→watch(isOpen, newValue => {
   302→  if (newValue) calculatePosition();
   303→});
   304→</script>
