  public static List<PropertyDescriptor> getAllDescriptors() {
    List<PropertyDescriptor> descriptors = new ArrayList<>();
    for (Field field : DefaultProperty.class.getDeclaredFields()) {
      if (
        Modifier.isStatic(field.getModifiers()) &&
        field.getType().equals(PropertyDescriptor.class)
      ) {
        try {
          descriptors.add((PropertyDescriptor) field.get(null));
        } catch (IllegalAccessException ignored) {}
      }
    }

    // Return duplicates so that each project has its own instance
    return descriptors
      .stream()
      .map(PropertyDescriptor::duplicate)
      .collect(Collectors.toList());
  }

  /**
   * Find a PropertyDescriptor by its key
   * @param key the property key to search for
   * @return the PropertyDescriptor with the matching key, or null if not found
   */
  public static PropertyDescriptor getByKey(String key) {
    if (key == null) {
      return null;
    }

    for (Field field : DefaultProperty.class.getDeclaredFields()) {
      if (
        Modifier.isStatic(field.getModifiers()) &&
        field.getType().equals(PropertyDescriptor.class)
      ) {
        try {
          PropertyDescriptor descriptor = (PropertyDescriptor) field.get(null);
          if (key.equals(descriptor.getKey())) {
            return descriptor;
          }
        } catch (IllegalAccessException e) {
          log.warn(
            "Failed to access PropertyDescriptor field: " +
            field.getName() +
            " - " +
            e.getMessage()
          );
        }
      }
    }
    return null;
  }
}

