const groupedOptions = computed<GroupedOptions>(() => {
  const ungrouped: FacetOption[] = [];
  const groups: Record<string, { options: FacetOption[]; expanded: boolean; selectedCount: number; totalCount: number }> = {};

  internalOptions.value.forEach(option => {
    if (!option.group) {
      ungrouped.push(option);
    } else {
      if (!groups[option.group]) {
        groups[option.group] = {
          options: [],
          expanded: groupExpansionState.value[option.group] ?? true,
          selectedCount: 0,
          totalCount: 0,
        };
      }
      groups[option.group].options.push(option);
      groups[option.group].totalCount++;
      if (option.selected) {
        groups[option.group].selectedCount++;
      }
    }
  });

  return { ungrouped, groups };
});

const filteredOptions = computed(() => {
  if (!searchQuery.value) return groupedOptions.value;

  const query = searchQuery.value.toLowerCase();
  const ungrouped = groupedOptions.value.ungrouped.filter(option =>
    option.label.toLowerCase().includes(query),
  );

  const groups: Record<string, { options: FacetOption[]; expanded: boolean; selectedCount: number; totalCount: number }> = {};
  Object.entries(groupedOptions.value.groups).forEach(([groupName, groupData]) => {
    const filteredGroupOptions = groupData.options.filter(option =>
      option.label.toLowerCase().includes(query),
    );

    if (filteredGroupOptions.length > 0) {
      groups[groupName] = {
        ...groupData,
        options: filteredGroupOptions,
      };
    }
  });

  return { ungrouped, groups };
});

