// Create a grouped structure for displaying facets with group headers
interface GroupedFacets {
  ungrouped: FacetItem[];
  grouped: {
    [groupName: string]: {
      facets: FacetItem[];
      expanded: boolean;
    };
  };
}

// Consolidate facet state into grouped structure
const groupedFacets = computed((): GroupedFacets => {
  const metadata = props.facetMetadata;
  const facetOrder = props.getFacetOrder(props.facets.facetData.value?.facets);
  const facetData = props.facets.facetData.value?.facets ?? {};

  // Helper function to create a FacetItem from facet data
  const createFacetItem = (
    facetKey: string,
    aggregateProperties: any,
  ): FacetItem => {
    const displayName = metadata[facetKey]?.displayName ?? facetKey;
    const {
      sortDirection,
      visibleRowCount,
      queryString: bucketQueryString,
    } = props.facets.getCategoricalFacetDisplayState(facetKey);

    const selectedBucketKeys =
      props.facets.pendingCategoricalFacets?.value?.[facetKey] ?? [];

    return {
      facetKey,
      displayName,
      isExpanded: props.facets.isFacetExpanded(facetKey),
      aggregateProperties,
      isHistogramFacet: props.facets.isHistogramFacet(aggregateProperties),
      selectedBucketKeys,
      sortDirection,
      visibleRowCount,
      queryString: bucketQueryString,
      extractionOptions: {
        isExtractable: props.facetMetadata[facetKey]?.isExtractable ?? false,
        isExtracting: false, // todo: unused https://github.com/unchartedsoftware/atlantis/issues/558
        isExtracted: false, // todo: unused https://github.com/unchartedsoftware/atlantis/issues/558
        extractionProgress: 0, // todo: unused https://github.com/unchartedsoftware/atlantis/issues/558
      },
    };
  };

  // Process facets in the user's preferred order
  const orderedFacets = facetOrder
    .map(facetKey => {
      const aggregateProperties = facetData[facetKey];
      return aggregateProperties
        ? createFacetItem(facetKey, aggregateProperties)
        : null;
    })
    .filter((facet): facet is FacetItem => facet !== null);

  // Add any remaining facets that aren't in the order (fallback for new facets)
  const remainingFacets = Object.entries(facetData)
    .filter(([facetKey]) => !facetOrder.includes(facetKey))
    .map(([facetKey, aggregateProperties]) =>
      createFacetItem(facetKey, aggregateProperties),
    );

  const allFacets = [...orderedFacets, ...remainingFacets];

  // Group facets by their group property
  const result: GroupedFacets = {
    ungrouped: [],
    grouped: {},
  };

  for (const facet of allFacets) {
    const group = metadata[facet.facetKey]?.group;

    if (!group) {
      result.ungrouped.push(facet);
    } else {
      if (!result.grouped[group]) {
        result.grouped[group] = {
          facets: [],
          expanded: true, // Groups are expanded by default
        };
      }
      result.grouped[group].facets.push(facet);
    }
  }

  return result;
});

// Backward compatibility - provide flat list for parts that still need it
const formattedFacets = computed((): FacetItem[] => {
  return [
    ...groupedFacets.value.ungrouped,
    ...Object.values(groupedFacets.value.grouped).flatMap(group => group.facets),
  ];
});

