// Create a grouped structure for displaying document facets with group headers
interface GroupedDocumentFacets {
  ungrouped: FacetItem[];
  grouped: {
    [groupName: string]: {
      facets: FacetItem[];
      expanded: boolean;
    };
  };
}

const groupedRegularFacets = computed((): GroupedDocumentFacets => {
  if (!props.document || !props.document?.propertyMap) {
    return { ungrouped: [], grouped: {} };
  }

  const facetOrder =
    props.getFacetOrder?.(props.facets.facetData.value?.facets) || [];
  const facetData = props.facets.facetData.value?.facets ?? {};

  // Helper function to create a facet item
  const createFacetItem = (
    facetKey: string,
    aggregateProperties: any,
  ): FacetItem => {
    const displayName = props.facetMetadata[facetKey]?.displayName ?? facetKey;
    const {
      sortDirection,
      visibleRowCount,
      queryString: bucketQueryString,
    } = props.facets.getCategoricalFacetDisplayState(facetKey);

    return {
      facetKey,
      displayName,
      isExpanded: props.facets.isFacetExpanded(facetKey),
      aggregateProperties,
      selectedBucketKeys: props.facets.selectedFacets.value[facetKey] ?? [],
      sortDirection,
      visibleRowCount,
      queryString: bucketQueryString,
      isHistogramFacet: false,
      extractionOptions: {
        isExtractable: props.facetMetadata[facetKey].isExtractable ?? false,
        isExtracting: false,
        isExtracted: false,
        extractionProgress: 0,
      },
    };
  };

  // Create facets in the user's preferred order, then add any missing ones
  const allFacetKeys = Object.keys(facetData).filter(key => key !== 'maps');
  const orderedKeys = facetOrder.filter(key => allFacetKeys.includes(key));
  const remainingKeys = allFacetKeys.filter(key => !orderedKeys.includes(key));

  const allFacets = [...orderedKeys, ...remainingKeys]
    .map(facetKey => {
      const aggregateProperties = facetData[facetKey];
      return aggregateProperties
        ? createFacetItem(facetKey, aggregateProperties)
        : null;
    })
    .filter((facet): facet is FacetItem => facet !== null);

  // Group facets by their group property
  const result: GroupedDocumentFacets = {
    ungrouped: [],
    grouped: {},
  };

  for (const facet of allFacets) {
    const group = props.facetMetadata[facet.facetKey]?.group;

    if (!group) {
      result.ungrouped.push(facet);
    } else {
      if (!result.grouped[group]) {
        result.grouped[group] = {
          facets: [],
          expanded: true, // Groups are expanded by default
        };
      }
      result.grouped[group].facets.push(facet);
    }
  }

  return result;
});

// Backward compatibility - provide flat list for parts that still need it
const regularFacets = computed((): FacetItem[] => {
  return [
    ...groupedRegularFacets.value.ungrouped,
    ...Object.values(groupedRegularFacets.value.grouped).flatMap(group => group.facets),
  ];
});

