const pendingFacetRowCount = ref<number | undefined>(undefined);

// Track group expansion state
const groupExpansionState = ref<{ [groupName: string]: boolean }>({});

// Function to toggle group expansion
const toggleGroupExpansion = (groupName: string) => {
  if (!(groupName in groupExpansionState.value)) {
    groupExpansionState.value[groupName] = true; // Default to expanded
  }
  groupExpansionState.value[groupName] = !groupExpansionState.value[groupName];
};

// Function to check if group is expanded
const isGroupExpanded = (groupName: string): boolean => {
  return groupExpansionState.value[groupName] ?? true; // Default to expanded
};

