     1→<template>
     2→  <header
     3→    :class="{
     4→      'header-expanded': isExpanded,
     5→    }"
     6→  >
     7→    <div class="header-main-row">
     8→      <!-- Toggle Button -->
     9→      <Button
    10→        ref="toggleButtonRef"
    11→        text
    12→        rounded
    13→        size="small"
    14→        class="toggle-button facet-header-collapse-button"
    15→        :class="{ rotated: isExpanded }"
    16→        @click="toggleExpansion"
    17→      >
    18→        <template #icon>
    19→          <FontAwesomeIcon icon="fa fa-chevron-right" />
    20→        </template>
    21→      </Button>
    22→
    23→      <span
    24→        class="text-sm title cursor-pointer"
    25→        @click="emit('set-is-expanded', !isExpanded)"
    26→        @keydown="emit('set-is-expanded', !isExpanded)"
    27→      >
    28→        <span class="title-content">
    29→          <FontAwesomeIcon v-if="facetIcon" :icon="facetIcon" fixed-width />
    30→          <span class="title-text">{{ facetDisplayName }}</span>
    31→          <i
    32→            v-if="status === FileUploadStatusEnum.InProgress"
    33→            v-tooltip.right="tooltipText"
    34→            class="pi pi-sparkles pulse"
    35→            :class="{ 'extract-more': canExtractMore }"
    36→            @click.stop="emit('extract-facet')"
    37→            @keydown.enter.stop="emit('extract-facet')"
    38→          />
    39→          <i
    40→            v-else-if="shouldShowSparkles"
    41→            v-tooltip.right="tooltipText"
    42→            class="pi pi-sparkles"
    43→            :class="{ 'extract-more': canExtractMore }"
    44→            @click.stop="emit('extract-facet')"
    45→            @keydown.enter.stop="emit('extract-facet')"
    46→          />
    47→        </span>
    48→      </span>
    49→      <template v-if="shouldShowDropdown">
    50→        <Dropdown
    51→          v-if="isExpanded"
    52→          :model-value="selectedDropdownOption"
    53→          @update:model-value="onDropdownUpdate"
    54→          :options="options"
    55→          option-label="title"
    56→          class="dropdown-display-how-many"
    57→          panelClass="small-dropdown-panel"
    58→          ref="topXDropdown"
    59→        >
    60→          <template #value>
    61→            <span class="dropdown-total-count">
    62→              <!-- For numeric facets, show total count as the value -->
    63→              <template v-if="props.isNumeric">
    64→                {{ `${props.totalBucketCount}${canExtractMore ? '+' : ''}` }}
    65→              </template>
    66→              <!-- For categorical facets, show selected option -->
    67→              <template v-else>
    68→                {{ selectedDropdownOption.title }}
    69→                <span v-if="shouldShowCount">
    70→                  of
    71→                  {{ `${props.totalBucketCount}${canExtractMore ? '+' : ''}` }}
    72→                </span>
    73→                <span v-else>({{ props.totalBucketCount }} total)</span>
    74→              </template>
    75→            </span>
    76→          </template>
    77→          <template #option="{ option }">
    78→            <span
    79→              v-if="option.key === 'extract-more'"
    80→              class="extract-more-option"
    81→            >
    82→              <CircleProgress
    83→                v-tooltip.bottom="tooltipText"
    84→                :percentage="
    85→                  ((props.documentCountWithFacet || 0) /
    86→                    totalDocumentsInSearch) *
    87→                  100
    88→                "
    89→                pie-chart
    90→                class="mr-1"
    91→              />{{ option.title }}
    92→            </span>
    93→            <span
    94→              v-else
    95→              class="dropdown-option"
    96→              :class="{ 'aligned-with-icon': canExtractMore }"
    97→            >
    98→              {{ option.title }}
    99→            </span>
   100→          </template>
   101→        </Dropdown>
   102→
   103→        <span v-else class="dropdown-total-count pr-2">
   104→          {{ `${props.totalBucketCount}${canExtractMore ? '+' : ''}` }}
   105→        </span>
   106→      </template>
   107→    </div>
   108→
   109→    <div
   110→      v-if="isBucketSearchExpanded && isExpanded"
   111→      class="search-bar-container"
   112→    >
   113→      <SearchBar
   114→        :query-string="queryString"
   115→        search-mode="keyword"
   116→        :should-show-clear-button="!!queryString"
   117→        :should-search-on-keyup="false"
   118→        :should-show-search-mode-toggle="false"
   119→        placeholder-text="Search facets"
   120→        size="small"
   121→        class="search-bar"
   122→        @update:query-string="newValue => emit('update-query-string', newValue)"
   123→      />
   124→    </div>
   125→  </header>
   126→</template>
   127→
   128→<script setup lang="ts">
   129→import { FontAwesomeIcon } from '@fortawesome/vue-fontawesome';
   130→import Button from 'primevue/button';
   131→import Dropdown from 'primevue/dropdown';
   132→import { computed, ref, watch, ComponentPublicInstance } from 'vue';
   133→import useProjectStore from '@/stores/ProjectStore';
   134→import { FileUploadStatusEnum, PropertyTag } from '@/types/Types';
   135→import { getPropertyDescriptorsByTag } from '@/util/Document';
   136→import SearchBar from '../SearchBar.vue';
   137→import CircleProgress from '../CircleProgress.vue';
   138→
   139→const props = defineProps<{
   140→  facetKey: string;
   141→  isExpanded: boolean;
   142→  facetDisplayName: string;
   143→  facetIcon: string | undefined;
   144→  totalBucketCount: string | undefined;
   145→  documentCountWithFacet: number | undefined;
   146→  sortDirection: number;
   147→  visibleRowCount: number;
   148→  queryString: string;
   149→  isExtractable: boolean;
   150→  isNumeric: boolean;
   151→  status?: FileUploadStatusEnum;
   152→  isDocumentMode?: boolean;
   153→}>();
   154→
   155→interface FacetSortOption {
   156→  key: string;
   157→  title: string;
   158→  sortDirection: number;
   159→  visibleRowCount: number;
   160→}
   161→
   162→const emit = defineEmits<{
   163→  (e: 'set-is-expanded', newValue: boolean): void;
   164→  (e: 'set-option', newValue: FacetSortOption): void;
   165→  (e: 'update-query-string', newValue: string): void;
   166→  (e: 'extract-facet'): void;
   167→}>();
   168→
   169→const isBucketSearchExpanded = ref(false);
   170→const topXDropdown = ref<InstanceType<typeof Dropdown> | null>(null);
   171→const totalDocumentsInSearch = ref<number>(0);
   172→
   173→const projectStore = useProjectStore();
   174→
   175→const shouldShowSparkles = computed(() => {
   176→  const descriptors = getPropertyDescriptorsByTag(PropertyTag.Derived);
   177→  return descriptors.some(descriptor => descriptor.key === props.facetKey);
   178→});
   179→
   180→const canExtractMore = computed(
   181→  () =>
   182→    props.isExtractable &&
   183→    !props.isDocumentMode &&
   184→    props.documentCountWithFacet &&
   185→    props.documentCountWithFacet < totalDocumentsInSearch.value,
   186→);
   187→
   188→const hasMultipleOptions = computed(
   189→  () =>
   190→    !props.isNumeric &&
   191→    props.totalBucketCount &&
   192→    parseInt(props.totalBucketCount, 10) > 5,
   193→);
   194→
   195→const shouldShowDropdown = computed(
   196→  () => props.isNumeric || canExtractMore.value || hasMultipleOptions.value,
   197→);
   198→
   199→const tooltipText = computed(() => {
   200→  if (!canExtractMore.value) {
   201→    return '';
   202→  }
   203→  return `${props.facetDisplayName} were extracted from ${props.documentCountWithFacet} of ${totalDocumentsInSearch.value} documents.`;
   204→});
   205→
   206→const options = computed<FacetSortOption[]>(() => {
   207→  const categoricalOptions = [
   208→    { key: 'top-5', title: 'Top 5', sortDirection: -1, visibleRowCount: 5 },
   209→    { key: 'top-20', title: 'Top 20', sortDirection: -1, visibleRowCount: 20 },
   210→    { key: 'top-50', title: 'Top 50', sortDirection: -1, visibleRowCount: 50 },
   211→    {
   212→      key: 'bottom-5',
   213→      title: 'Bottom 5',
   214→      sortDirection: 1,
   215→      visibleRowCount: 5,
   216→    },
   217→    {
   218→      key: 'bottom-20',
   219→      title: 'Bottom 20',
   220→      sortDirection: 1,
   221→      visibleRowCount: 20,
   222→    },
   223→    {
   224→      key: 'bottom-50',
   225→      title: 'Bottom 50',
   226→      sortDirection: 1,
   227→      visibleRowCount: 50,
   228→    },
   229→    {
   230→      key: 'search-all',
   231→      title: 'Search all',
   232→      sortDirection: -1,
   233→      visibleRowCount: 50,
   234→    },
   235→  ];
   236→
   237→  const baseOptions = props.isNumeric ? [] : categoricalOptions;
   238→
   239→  if (canExtractMore.value) {
   240→    baseOptions.push({
   241→      key: 'extract-more',
   242→      title: 'Extract more...',
   243→      sortDirection: -1,
   244→      visibleRowCount: 50,
   245→    });
   246→  }
   247→
   248→  return baseOptions;
   249→});
   250→
   251→const selectedOption = computed(
   252→  () =>
   253→    options.value.find(
   254→      option =>
   255→        option.sortDirection === props.sortDirection &&
   256→        option.visibleRowCount === props.visibleRowCount,
   257→    ) ?? options.value[0],
   258→);
   259→
   260→const selectedDropdownOption = ref<FacetSortOption>(selectedOption.value);
   261→
   262→const shouldShowCount = computed(() => {
   263→  const bucketCount = props.totalBucketCount
   264→    ? parseInt(props.totalBucketCount, 10)
   265→    : 0;
   266→  const selectedKey = selectedDropdownOption.value.key;
   267→  const hideCountKeys = ['search-all', 'extract-more', 'show-all'];
   268→
   269→  return (
   270→    bucketCount > selectedOption.value.visibleRowCount &&
   271→    !hideCountKeys.includes(selectedKey)
   272→  );
   273→});
   274→
   275→const setTotalDocumentsInSearch = () => {
   276→  totalDocumentsInSearch.value = projectStore.totalDocumentCount ?? 0;
   277→};
   278→
   279→const toggleButtonRef = ref<ComponentPublicInstance | null>(null);
   280→
   281→const toggleExpansion = () => {
   282→  emit('set-is-expanded', !props.isExpanded);
   283→  if (toggleButtonRef.value && (toggleButtonRef.value as any).$el) {
   284→    // eslint-disable-next-line
   285→    toggleButtonRef.value.$el.blur();
   286→  }
   287→};
   288→
   289→const onDropdownUpdate = (newValue: FacetSortOption) => {
   290→  if (newValue.key === 'search-all') {
   291→    isBucketSearchExpanded.value = true;
   292→    selectedDropdownOption.value = newValue;
   293→    if (topXDropdown.value) topXDropdown.value.hide();
   294→  } else if (newValue.key === 'extract-more') {
   295→    if (topXDropdown.value) topXDropdown.value.hide();
   296→    emit('extract-facet');
   297→  } else {
   298→    isBucketSearchExpanded.value = false;
   299→    selectedDropdownOption.value = newValue;
   300→    emit('set-option', newValue);
   301→  }
   302→};
   303→
   304→// Keep in sync with selectedOption from props
   305→watch(selectedOption, val => {
   306→  selectedDropdownOption.value = val;
   307→});
   308→
   309→// Watch for project store changes to update document count
   310→watch(
   311→  [projectStore],
   312→  () => {
   313→    setTotalDocumentsInSearch();
   314→  },
   315→  { immediate: true },
   316→);
   317→</script>
