package software.uncharted.pantera.model.search;

import co.elastic.clients.elasticsearch._types.mapping.RuntimeFieldType;
import com.fasterxml.jackson.annotation.JsonIgnore;
import jakarta.persistence.CascadeType;
import jakarta.persistence.Column;
import jakarta.persistence.Entity;
import jakarta.persistence.EnumType;
import jakarta.persistence.Enumerated;
import jakarta.persistence.Id;
import jakarta.persistence.IdClass;
import jakarta.persistence.ManyToOne;
import jakarta.persistence.PrePersist;
import jakarta.persistence.PreUpdate;
import jakarta.persistence.Transient;
import java.io.Serializable;
import java.util.HashSet;
import java.util.List;
import java.util.Map;
import java.util.Set;
import lombok.Data;
import lombok.NoArgsConstructor;
import lombok.experimental.Accessors;
import org.hibernate.annotations.JdbcTypeCode;
import org.hibernate.type.SqlTypes;
import software.uncharted.pantera.model.project.Project;
import software.uncharted.pantera.util.DTOMerge;
import software.uncharted.pantera.util.NonNullMerge;
import software.uncharted.pipeline.common.TSModel;
import software.uncharted.pipeline.common.TSOptional;
import software.uncharted.pipeline.common.features.PropertyTag;

@Data
@NoArgsConstructor
@Accessors(chain = true)
@TSModel
@Entity
@IdClass(PropertyDescriptorId.class)
public class PropertyDescriptor implements DTOMerge, Serializable {

  @Id
  private String key;

  @Id
  @ManyToOne(cascade = { CascadeType.PERSIST, CascadeType.MERGE })
  @JsonIgnore
  private Project project;

  @NonNullMerge
  private String name;

  @Column(columnDefinition = "TEXT")
  @NonNullMerge
  private String mapping;

  @NonNullMerge
  @TSOptional
  private String icon;

  @TSOptional
  @Column(name = "\"group\"")
  private String group;

  @JdbcTypeCode(SqlTypes.JSON)
  @Enumerated(EnumType.STRING)
  @NonNullMerge
  private Set<PropertyTag> tags;

  public boolean containsTag(final PropertyTag tag) {
    return tags != null && tags.contains(tag);
  }

  public boolean containsAnyTag(final List<PropertyTag> tags) {
    if (this.tags == null || tags == null || tags.isEmpty()) {
      return false;
    }
    for (PropertyTag source : this.tags) {
      for (PropertyTag dest : tags) {
        if (source == dest) {
          return true;
        }
      }
    }
    return false;
  }

  /**
   * Get the mapping for this property descriptor, with the appropriate escape
   * characters for use in a URL.
   * <p>
   * Because the characters '~' (%x7E) and '/' (%x2F) have special
   * meanings in JSON Pointer, '~' needs to be encoded as '~0' and '/'
   * needs to be encoded as '~1' when these characters appear in a
   * reference token.
   *
   * @return The mapping for this property descriptor, with the appropriate escape
   *         characters for use in a URL.
   */
  @JsonIgnore
  public String getJacksonMapping() {
    if (this.mapping != null) {
      return (
        "/" +
        this.mapping.replace("~", "~0").replace("/", "~1").replace(".", "/")
      );
    }
    return null;
  }

  public String getName() {
    if (this.name != null) {
      return this.name;
    }
    return key;
  }

  @JsonIgnore
  public PropertyDescriptorId getId() {
    return new PropertyDescriptorId(key, project);
  }

  public PropertyDescriptor setTags(final Set<PropertyTag> tags) {
    this.tags = tags == null ? new HashSet<>() : new HashSet<>(tags);
    return this;
  }

  public PropertyDescriptor addTag(final PropertyTag tag) {
    if (this.tags == null) {
      final Set<PropertyTag> newTags = new HashSet<>();
      newTags.add(tag);
      this.tags = newTags;
    } else {
      this.tags.add(tag);
    }
    return this;
  }

  public PropertyDescriptor removeTag(final PropertyTag tag) {
    if (this.tags != null) {
      this.tags.remove(tag);
    }
    return this;
  }

  public static PropertyDescriptorId getId(
    final String key,
    final Project project
  ) {
    return new PropertyDescriptorId(key, project);
  }

  @JsonIgnore
  @Transient
  public RuntimeFieldType getRuntimeFieldType() {
    if (containsTag(PropertyTag.STRING)) {
      return RuntimeFieldType.Keyword;
    } else if (containsTag(PropertyTag.NUMBER)) {
      return RuntimeFieldType.Double;
    } else {
      throw new IllegalArgumentException(
        "Unsupported property type for runtime field: " +
        key +
        ". Must be tagged with STRING or NUMBER."
      );
    }
  }

  @JsonIgnore
  @Transient
  public String getRuntimeFieldSource(final Map<String, String> fieldMappings) {
    if (containsTag(PropertyTag.STRING)) {
      return "emit(doc['" + getMapping() + "'].value)";
    } else if (containsTag(PropertyTag.NUMBER)) {
      return "emit(doc['" + getMapping() + "'].value)";
    } else {
      throw new IllegalArgumentException(
        "Unsupported property type for runtime field: " +
        key +
        ". Must be tagged with STRING or NUMBER."
      );
    }
  }

  /**
   * Create a duplicate of this PropertyDescriptor.
   * @return  the duplicated PropertyDescriptor without the project reference.
   */
  public PropertyDescriptor duplicate() {
    return new PropertyDescriptor()
      .setKey(this.key)
      .setProject(this.project)
      .setName(this.name)
      .setMapping(this.mapping)
      .setIcon(this.icon)
      .setGroup(this.group)
      .setTags(new HashSet<>(this.tags));
  }

  @PreUpdate
  @PrePersist
  public void validate() {
    if (key == null || key.isBlank()) {
      throw new IllegalArgumentException("Key cannot be null or empty");
    }
    // Key must be lowercase alphanumeric (with addition of _ )
    if (!key.matches("^[a-z0-9_]+$")) {
      throw new IllegalArgumentException(
        "Key must be lowercase alphanumeric with underscores"
      );
    }
  }
}

