import {
  AggregateProperties,
  CategoricalFacetDisplayState,
  PropertyTag,
  SavedFacet,
  SelectionInterval,
} from '@/types/Types';
import formatDate from './DateFormatter';

export interface FacetMetadata {
  displayName: string;
  icon?: string;
  isExpanded: boolean;
  isExtractable?: boolean;
  tags?: PropertyTag[];
  group?: string;
}

export interface CategoricalFacetState {
  [facetKey: string]: CategoricalFacetDisplayState & {
    selectedBucketKeys: string[];
  };
}

export interface NumericFacetSelectionState {
  [facetKey: string]: { intervalMin: number; intervalMax: number };
}

export type HistogramChartDatum = {
  min: number;
  max: number;
  minFormatted: string;
  maxFormatted: string;
  count: number;
};

export enum FacetType {
  Categorical = 'categorical',
  Numeric = 'numeric',
  Extractable = 'extractable',
}

// Base facet interface
export interface FacetItem {
  facetKey: string;
  displayName: string;
  isExpanded: boolean;
  aggregateProperties: AggregateProperties;
  isHistogramFacet: boolean;
  selectedBucketKeys: string[];
  sortDirection: number;
  visibleRowCount: number;
  queryString: string;
  tags?: PropertyTag[];
  icon?: string;
  isExtractable: boolean;
}

export interface FacetSelectionListItem {
  facetKey: string;
  summary: string;
  facetType: FacetType;
  displayName: string;
}

export enum SortOrder {
  Descending = -1,
  Ascending = 1,
}

export const DEFAULT_SORT_DIRECTION = SortOrder.Descending;
export const DEFAULT_VISIBLE_ROW_COUNT = 5;

/**
 * Convert selected categorical facet buckets into lucene query terms.
 */
const convertCategoricalFacetsToLuceneQueryTerms = (
  categoricalFacetState: CategoricalFacetState,
) =>
  Object.entries(categoricalFacetState)
    .map(([categoricalFacetKey, { selectedBucketKeys }]) => {
      let termsForThisFacet = selectedBucketKeys
        // Wrap bucket key in quotes in case it's multiple words
        .map(bucketKey => `${categoricalFacetKey}:"${bucketKey}"`)
        .join(' OR ');
      if (selectedBucketKeys.length > 1) {
        termsForThisFacet = `(${termsForThisFacet})`;
      }
      return selectedBucketKeys.length > 0 ? termsForThisFacet : null;
    })
    .filter(term => term !== null) as string[];

/**
 * Convert selected numeric facet buckets into lucene range query terms.
 */
const convertNumericFacetsToLuceneQueryTerms = (
  numericFacetSelectionState: NumericFacetSelectionState,
) =>
  Object.entries(numericFacetSelectionState).map(
    ([numericFacetKey, { intervalMin, intervalMax }]) =>
      `${numericFacetKey}:[${intervalMin} TO ${intervalMax}]`,
  );

/**
 * Converts the selection state of each facet into a lucene query.
 * Terms from the same facet are OR'd together. e.g. carColour:red OR carColour:blue
 * Terms from different facets are AND'd together. e.g. carColour:blue AND wheelCount:4
 */
const convertFacetSelectionStateToLuceneQuery = (
  categoricalFacetState: CategoricalFacetState,
  numericFacetSelectionState: NumericFacetSelectionState,
) => {
  const terms = [
    ...convertCategoricalFacetsToLuceneQueryTerms(categoricalFacetState),
    ...convertNumericFacetsToLuceneQueryTerms(numericFacetSelectionState),
  ];
  return terms.join(' AND ');
};

/**
 * Finds the buckets that are contained entirely within the provided interval, then returns the
 * lowest selected bucket's minimum value and the greatest selected bucket's maximum value.
 * Returns null if no buckets fall completely within the interval.
 * @param interval A valid pair of numbers where the first must be lower than the second.
 * @param allBuckets A list of all possible buckets in the facet.
 */
const shrinkIntervalToSelectedBuckets = (
  interval: [number, number],
  allBuckets: HistogramChartDatum[],
): SelectionInterval | null => {
  const [intervalMin, intervalMax] = interval;

  const selectedBuckets = allBuckets.filter(
    ({ min, max }) => min >= intervalMin && max <= intervalMax,
  );
  if (selectedBuckets.length === 0) {
    return null;
  }
  return {
    intervalMin: Math.min(...selectedBuckets.map(bucket => bucket.min)),
    intervalMax: Math.max(...selectedBuckets.map(bucket => bucket.max)),
  };
};

const serializeFacetState = (
  categoricalFacetState: CategoricalFacetState,
  numericFacetSelectionState: NumericFacetSelectionState,
) =>
  encodeURIComponent(
    JSON.stringify({
      categoricalFacetState,
      numericFacetSelectionState,
    }),
  );

const parseFacetState = (
  serializedState: string,
): {
  categoricalFacetState: CategoricalFacetState;
  numericFacetSelectionState: NumericFacetSelectionState;
} => JSON.parse(decodeURIComponent(serializedState));

const convertCategoricalFacetsToList = (
  categoricalFacetState: CategoricalFacetState,
  metadata: { [facetKey: string]: FacetMetadata },
): FacetSelectionListItem[] =>
  Object.entries(categoricalFacetState)
    .map<FacetSelectionListItem>(([facetKey, { selectedBucketKeys }]) => ({
      facetKey,
      summary: selectedBucketKeys.join(' OR '),
      facetType: FacetType.Categorical,
      displayName: metadata[facetKey]?.displayName ?? facetKey,
    }))
    .filter(({ summary }) => summary.length > 0);

const convertNumericFacetsToList = (
  numericFacetSelectionState: NumericFacetSelectionState,
  facetsThatRequireDateFormatting: string[],
  metadata: { [facetKey: string]: FacetMetadata },
): FacetSelectionListItem[] =>
  Object.entries(numericFacetSelectionState).map<FacetSelectionListItem>(
    ([facetKey, { intervalMin, intervalMax }]) => {
      const summary = facetsThatRequireDateFormatting.includes(facetKey)
        ? `${formatDate(intervalMin, true)} to ${formatDate(intervalMax, true)}`
        : `${intervalMin} to ${intervalMax}`;
      return {
        facetKey,
        summary,
        facetType: FacetType.Numeric,
        displayName: metadata[facetKey]?.displayName ?? facetKey,
      };
    },
  );

const convertFacetSelectionStateToList = (
  categoricalFacetState: CategoricalFacetState,
  numericFacetSelectionState: NumericFacetSelectionState,
  facetsThatRequireDateFormatting: string[],
  facetMetadata: { [facetKey: string]: FacetMetadata },
): FacetSelectionListItem[] => [
  ...convertCategoricalFacetsToList(categoricalFacetState, facetMetadata),
  ...convertNumericFacetsToList(
    numericFacetSelectionState,
    facetsThatRequireDateFormatting,
    facetMetadata,
  ),
];

const convertFacetStatesToSavedFacet = (
  facetState: CategoricalFacetState | NumericFacetSelectionState,
  facetType: FacetType,
): SavedFacet[] =>
  Object.entries(facetState).map(
    ([facetKey, { selectedBucketKeys, intervalMin, intervalMax }]) => {
      const savedFacet: SavedFacet = {
        facetKey,
        facetType,
      };
      if (selectedBucketKeys) {
        savedFacet.selectedBucketKeys = selectedBucketKeys;
      }
      if (intervalMin !== undefined && intervalMax !== undefined) {
        savedFacet.selectionInterval = {
          intervalMin,
          intervalMax,
        };
      }
      return savedFacet;
    },
  );

const convertSavedFacetsToFacetSelectionState = (
  savedFacets: SavedFacet[],
): {
  categoricalFacetState: CategoricalFacetState;
  numericalFacetState: NumericFacetSelectionState;
} => {
  const categoricalFacetState: CategoricalFacetState = {};
  const numericalFacetState: NumericFacetSelectionState = {};

  savedFacets.forEach(facet => {
    if (facet.facetType === FacetType.Categorical) {
      categoricalFacetState[facet.facetKey] = {
        selectedBucketKeys: facet.selectedBucketKeys ?? [],
        sortDirection: DEFAULT_SORT_DIRECTION,
        visibleRowCount: DEFAULT_VISIBLE_ROW_COUNT,
        queryString: '',
        includes: [],
      };
    } else if (facet.facetType === FacetType.Numeric) {
      numericalFacetState[facet.facetKey] = {
        intervalMin: facet.selectionInterval?.intervalMin ?? 0,
        intervalMax: facet.selectionInterval?.intervalMax ?? 0,
      };
    }
  });

  return { categoricalFacetState, numericalFacetState };
};

const concatLuceneQueries = (firstQuery: string, secondQuery: string) =>
  [firstQuery, secondQuery]
    .filter(Boolean)
    .map(query => `(${query})`)
    .join(' AND ')
    .trim();

export default {
  convertCategoricalFacetsToLuceneQueryTerms,
  convertNumericFacetsToLuceneQueryTerms,
  convertFacetSelectionStateToLuceneQuery,
  shrinkIntervalToSelectedBuckets,
  serializeFacetState,
  parseFacetState,
  convertCategoricalFacetsToList,
  convertNumericFacetsToList,
  convertFacetSelectionStateToList,
  convertFacetStatesToSavedFacet,
  convertSavedFacetsToFacetSelectionState,
  concatLuceneQueries,
};

