// Input validation
if (params == null || params.deletionCriteria == null || params.deletionCriteria.isEmpty()) {
  return;
}

// Note: Using inline logic instead of function definitions (Painless doesn't support user functions in update-by-query)

for (criteria in params.deletionCriteria) {
  if (criteria.propertyKey == null || criteria.propertyKey.isEmpty()) {
    continue;
  }

  // Validate range values if both are provided
  if (criteria.minValue != null && criteria.maxValue != null) {
    try {
      double min = Double.parseDouble(criteria.minValue);
      double max = Double.parseDouble(criteria.maxValue);
      if (min > max) {
        continue;
      }
    } catch (Exception e) {
      // For non-numeric values, use string comparison
      if (criteria.minValue.compareTo(criteria.maxValue) > 0) {
        continue;
      }
    }
  }

  // Handle _feature namespaces - process ALL namespaces (no early breaks)
  for (namespace in ctx._source.keySet()) {
    if (!namespace.endsWith('_feature')) {
      continue;
    }

    if (ctx._source[namespace] != null && ctx._source[namespace].containsKey(criteria.propertyKey)) {
      def originalFieldValue = ctx._source[namespace][criteria.propertyKey];

      if (originalFieldValue == null) {
        continue;
      }

      boolean shouldRemove = false;

      // Handle arrays and single values
      if (originalFieldValue instanceof List) {
        def fieldValue = new ArrayList(originalFieldValue);
        int originalSize = fieldValue.size();

        if (criteria.exactValue != null) {
          // Use explicit loop instead of removeIf for Painless compatibility
          for (int i = fieldValue.size() - 1; i >= 0; i--) {
            def item = fieldValue.get(i);
            if (item != null && String.valueOf(item).equals(criteria.exactValue)) {
              fieldValue.remove(i);
            }
          }
        } else if (criteria.minValue != null || criteria.maxValue != null) {
          // Use explicit loop for range-based removal
          for (int i = fieldValue.size() - 1; i >= 0; i--) {
            def item = fieldValue.get(i);
            if (item != null) {
              boolean inRange = false;
              // Try numeric comparison first
              try {
                double numValue = Double.parseDouble(String.valueOf(item));
                double min = criteria.minValue != null ? Double.parseDouble(criteria.minValue) : Double.NEGATIVE_INFINITY;
                double max = criteria.maxValue != null ? Double.parseDouble(criteria.maxValue) : Double.POSITIVE_INFINITY;
                inRange = numValue >= min && numValue <= max;
              } catch (Exception e) {
                // Fall back to string comparison
                String strValue = String.valueOf(item);
                inRange = true;
                if (criteria.minValue != null) {
                  inRange = strValue.compareTo(criteria.minValue) >= 0;
                }
                if (inRange && criteria.maxValue != null) {
                  inRange = strValue.compareTo(criteria.maxValue) <= 0;
                }
              }
              if (inRange) {
                fieldValue.remove(i);
              }
            }
          }
        }

        if (fieldValue.size() < originalSize) {
          ctx._source[namespace][criteria.propertyKey] = fieldValue;

          // Keep empty arrays instead of removing the property entirely
          // This preserves document structure and prevents document exclusion
        }
      } else {
        // Handle single values
        if (criteria.exactValue != null) {
          shouldRemove = String.valueOf(originalFieldValue).equals(criteria.exactValue);
        } else if (criteria.minValue != null || criteria.maxValue != null) {
          // Handle range removal for single values
          boolean inRange = false;
          // Try numeric comparison first
          try {
            double numValue = Double.parseDouble(String.valueOf(originalFieldValue));
            double min = criteria.minValue != null ? Double.parseDouble(criteria.minValue) : Double.NEGATIVE_INFINITY;
            double max = criteria.maxValue != null ? Double.parseDouble(criteria.maxValue) : Double.POSITIVE_INFINITY;
            inRange = numValue >= min && numValue <= max;
          } catch (Exception e) {
            // Fall back to string comparison
            String strValue = String.valueOf(originalFieldValue);
            inRange = true;
            if (criteria.minValue != null) {
              inRange = strValue.compareTo(criteria.minValue) >= 0;
            }
            if (inRange && criteria.maxValue != null) {
              inRange = strValue.compareTo(criteria.maxValue) <= 0;
            }
          }
          shouldRemove = inRange;
        }
      }

      if (shouldRemove) {
        ctx._source[namespace].remove(criteria.propertyKey);
      }
    }
  }
}

