void testBulkRemovePropertyValues_BooleanValues()
  throws IOException, InterruptedException {
  final String index = randomIndexName();
  elasticsearchService.createIndex(index);

  final List<JsonNode> testData = List.of(
    objectMapper.readTree(
      """
      {
        "keyword_feature": {
          "flags": [true, false, "true", "false"]
        }
      }
      """
    )
  );

  final List<String> documentIds = List.of("doc1");
  elasticsearchService.insert(index, testData, documentIds);
  elasticsearchService.refresh(index);

  // Remove boolean true (should match both boolean true and string "true")
  final List<Object> criteria = List.of(
    new TestDeletionCriteria("flags", "true", null, null)
  );
  final String taskId = elasticsearchService.bulkRemovePropertyValues(
    index,
    criteria,
    documentIds
  );

  Assertions.assertNotNull(taskId);
  Assertions.assertDoesNotThrow(() ->
    elasticsearchService.waitForTaskCompletion(taskId, 10).join()
  );

  elasticsearchService.refresh(index);

  final SearchResponse<JsonNode> response = elasticsearchService.search(
    index,
    Query.of(b -> b.matchAll(m -> m)),
    JsonNode.class
  );

  final JsonNode doc = response.hits().hits().get(0).source();
  final JsonNode flags = doc.at("/keyword_feature/flags");

  // Should keep false and "false" (string conversion matches only "true")
  Assertions.assertEquals(2, flags.size());
  Assertions.assertEquals(false, flags.get(0).asBoolean());
  Assertions.assertEquals("false", flags.get(1).asText());
}

