  @Test
  public void testItCanUpsertMixedNewAndExistingDocuments()
    throws IOException, InterruptedException {
    final String name = randomIndexName();
    elasticsearchService.createIndex(name);

    // Insert initial document
    final List<JsonNode> initialData = List.of(
      objectMapper
        .createObjectNode()
        .put("testField", "existing")
        .put("title", "Existing Document")
    );
    final List<String> existingIds = List.of("existing-doc");

    elasticsearchService.upsert(name, initialData, existingIds);
    Thread.sleep(1000);
    elasticsearchService.refresh(name);

    // Verify initial state
    SearchResponse<JsonNode> initialResponse = elasticsearchService.search(
      name,
      Query.of(b -> b.matchAll(m -> m)),
      JsonNode.class
    );
    Assertions.assertEquals(1L, initialResponse.hits().total().value());

    // Upsert with both new and existing documents
    final List<JsonNode> mixedData = List.of(
      objectMapper
        .createObjectNode()
        .put("testField", "existing-updated")
        .put("title", "Updated Existing Document"), // Update existing
      objectMapper
        .createObjectNode()
        .put("testField", "new")
        .put("title", "New Document") // Insert new
    );
    final List<String> mixedIds = List.of("existing-doc", "new-doc");

    elasticsearchService.upsert(name, mixedData, mixedIds);
    Thread.sleep(1000);
    elasticsearchService.refresh(name);

    // Verify both documents exist with proper full_text_search_data
    SearchResponse<JsonNode> finalResponse = elasticsearchService.search(
      name,
      Query.of(b -> b.matchAll(m -> m)),
      JsonNode.class
    );
    Assertions.assertEquals(2L, finalResponse.hits().total().value());

    // Verify both documents have full_text_search_data
    finalResponse
      .hits()
      .hits()
      .forEach(hit -> {
        ArrayNode fullTextData = (ArrayNode) hit
          .source()
          .at("/analyzed_feature/full_text_search_data");
        Assertions.assertTrue(
          fullTextData.size() > 0,
          "Document should have full_text_search_data"
        );

        List<String> searchData = new ArrayList<>();
        fullTextData.forEach(node -> searchData.add(node.asText()));

        if (hit.id().equals("existing-doc")) {
          // Updated existing document
          boolean containsUpdated = searchData
            .stream()
            .anyMatch(s -> s.contains("existing-updated"));
          boolean containsUpdatedTitle = searchData
            .stream()
            .anyMatch(s -> s.contains("Updated Existing"));
          Assertions.assertTrue(
            containsUpdated,
            "Updated document should contain new values"
          );
          Assertions.assertTrue(
            containsUpdatedTitle,
            "Updated document should contain new title"
          );
        } else if (hit.id().equals("new-doc")) {
          // New document
          boolean containsNew = searchData
            .stream()
            .anyMatch(s -> s.contains("new"));
          boolean containsNewTitle = searchData
            .stream()
            .anyMatch(s -> s.contains("New Document"));
          Assertions.assertTrue(
            containsNew,
            "New document should contain its values"
          );
          Assertions.assertTrue(
            containsNewTitle,
            "New document should contain its title"
          );
        }
      });
  }

  @Test
  public void testFullTextSearchDataContainsAllDocumentFields()
    throws IOException, InterruptedException {
    final String name = randomIndexName();
    elasticsearchService.createIndex(name);

    // Create document with various field types
    final ObjectNode complexDocument = objectMapper
      .createObjectNode()
      .put("stringField", "test string")
      .put("numberField", 42)
      .put("booleanField", true);

    // Add array field
    complexDocument.set(
      "arrayField",
      objectMapper.createArrayNode().add("item1").add("item2").add("item3")
    );

    // Add object field
    complexDocument.set(
      "objectField",
      objectMapper
        .createObjectNode()
        .put("nestedString", "nested value")
        .put("nestedNumber", 123)
    );

    elasticsearchService.upsert(
      name,
      List.of(complexDocument),
      List.of("complex-doc")
    );
    Thread.sleep(1000);
    elasticsearchService.refresh(name);

    // Verify full_text_search_data contains all field values
    SearchResponse<JsonNode> response = elasticsearchService.search(
      name,
      Query.of(b -> b.matchAll(m -> m)),
      JsonNode.class
    );
    Assertions.assertEquals(1L, response.hits().total().value());

    Hit<JsonNode> hit = response.hits().hits().get(0);
    ArrayNode fullTextData = (ArrayNode) hit
      .source()
      .at("/analyzed_feature/full_text_search_data");
    List<String> searchData = new ArrayList<>();
    fullTextData.forEach(node -> searchData.add(node.asText()));

    // Verify various field types are included
    Assertions.assertTrue(
      searchData.contains("test string"),
      "Should contain string field"
    );
    Assertions.assertTrue(
      searchData.contains("42"),
      "Should contain number field as string"
    );
    Assertions.assertTrue(
      searchData.contains("true"),
      "Should contain boolean field as string"
    );
    Assertions.assertTrue(
      searchData.contains("item1"),
      "Should contain array items"
    );
    Assertions.assertTrue(
      searchData.contains("item2"),
      "Should contain array items"
    );
    Assertions.assertTrue(
      searchData.contains("item3"),
      "Should contain array items"
    );
    Assertions.assertTrue(
      searchData.contains("nested value"),
      "Should contain nested object values"
    );
    Assertions.assertTrue(
      searchData.contains("123"),
      "Should contain nested number as string"
    );

    // Verify analyzed_feature fields are NOT included
    boolean containsAnalyzedFeature = searchData
      .stream()
      .anyMatch(
        s ->
          s.contains("analyzed_feature") || s.contains("full_text_search_data")
      );
    Assertions.assertFalse(
      containsAnalyzedFeature,
      "Should not contain analyzed_feature data"
    );
  }

  /**
