  @Test
  void testBulkRemovePropertyValues_SuccessfulRemoval()
    throws IOException, InterruptedException {
    final String index = randomIndexName();
    elasticsearchService.createIndex(index);

    // Create test documents with _feature namespaces
    final List<JsonNode> testData = List.of(
      objectMapper.readTree(
        """
        {
          "title": "Document 1",
          "keyword_feature": {
            "category": ["science", "technology"],
            "author": "John Doe"
          }
        }
        """
      ),
      objectMapper.readTree(
        """
        {
          "title": "Document 2",
          "keyword_feature": {
            "category": ["science", "history"],
            "language": "English"
          }
        }
        """
      ),
      objectMapper.readTree(
        """
        {
          "title": "Document 3",
          "keyword_feature": {
            "author": "Jane Smith",
            "language": "French"
          }
        }
        """
      )
    );

    final List<String> documentIds = List.of("doc1", "doc2", "doc3");
    elasticsearchService.insert(index, testData, documentIds);
    elasticsearchService.refresh(index);

    // Debug: Check document structure before removal
    SearchResponse<JsonNode> beforeResponse = elasticsearchService.search(
      index,
      Query.of(b -> b.matchAll(m -> m)),
      JsonNode.class
    );
    System.out.println("=== BEFORE REMOVAL ===");
    for (Hit<JsonNode> hit : beforeResponse.hits().hits()) {
      System.out.println("Doc " + hit.id() + ": " + hit.source().toString());
    }

    // Remove "science" values from "category" property in doc1 and doc2
    final List<Object> criteria = List.of(
      new TestDeletionCriteria("category", "science", null, null)
    );
    final String taskId = elasticsearchService.bulkRemovePropertyValues(
      index,
      criteria,
      List.of("doc1", "doc2")
    );

    Assertions.assertNotNull(taskId);
    Assertions.assertFalse(taskId.isEmpty());

    // Wait for task completion
    Assertions.assertDoesNotThrow(() ->
      elasticsearchService.waitForTaskCompletion(taskId, 10).join()
    );

    elasticsearchService.refresh(index);

    // Verify results
    final SearchResponse<JsonNode> response = elasticsearchService.search(
      index,
      Query.of(b -> b.matchAll(m -> m)),
      JsonNode.class
    );

    System.out.println("=== AFTER REMOVAL ===");
    for (Hit<JsonNode> hit : response.hits().hits()) {
      System.out.println("Doc " + hit.id() + ": " + hit.source().toString());
      // Check for debug fields to see if script executed
      JsonNode debugExecuted = hit.source().get("debug_script_executed");
      System.out.println("Script executed: " + (debugExecuted != null ? debugExecuted.asBoolean() : "false"));
    }

    Assertions.assertEquals(3L, response.hits().total().value());

    // Check doc1: should have removed "science" but kept "technology" and "author"
    final JsonNode doc1 = response
      .hits()
      .hits()
      .stream()
      .filter(hit -> hit.id().equals("doc1"))
      .findFirst()
      .orElseThrow()
      .source();

    final JsonNode doc1KeywordFeature = doc1.get("keyword_feature");
    Assertions.assertTrue(doc1KeywordFeature.isObject());

    final JsonNode doc1Category = doc1KeywordFeature.get("category");
    if (doc1Category != null && doc1Category.isArray()) {
      System.out.println("Doc1 category array size: " + doc1Category.size());
      for (int i = 0; i < doc1Category.size(); i++) {
        System.out.println("Doc1 category[" + i + "]: " + doc1Category.get(i).asText());
      }
      Assertions.assertEquals(1, doc1Category.size());
      Assertions.assertEquals("technology", doc1Category.get(0).asText());
    } else {
      System.out.println("Doc1 category is null or not an array: " + doc1Category);
      Assertions.fail("Doc1 category should be an array with one element");
    }

    final JsonNode doc1Author = doc1KeywordFeature.get("author");
    Assertions.assertEquals("John Doe", doc1Author.asText());

    // Check doc2: should have removed "science" but kept "history" and "language"
    final JsonNode doc2 = response
      .hits()
      .hits()
      .stream()
      .filter(hit -> hit.id().equals("doc2"))
      .findFirst()
      .orElseThrow()
      .source();

    final JsonNode doc2KeywordFeature = doc2.get("keyword_feature");
    Assertions.assertTrue(doc2KeywordFeature.isObject());

    final JsonNode doc2Category = doc2KeywordFeature.get("category");
    if (doc2Category != null && doc2Category.isArray()) {
      Assertions.assertEquals(1, doc2Category.size());
      Assertions.assertEquals("history", doc2Category.get(0).asText());
    } else {
      Assertions.fail("Doc2 category should be an array with one element");
    }

    final JsonNode doc2Language = doc2KeywordFeature.get("language");
    Assertions.assertEquals("English", doc2Language.asText());

    // Check doc3: should be unchanged (wasn't in target document list)
    final JsonNode doc3 = response
      .hits()
      .hits()
      .stream()
      .filter(hit -> hit.id().equals("doc3"))
      .findFirst()
      .orElseThrow()
      .source();

    final JsonNode doc3KeywordFeature = doc3.get("keyword_feature");
    Assertions.assertTrue(doc3KeywordFeature.isObject());
    Assertions.assertEquals(2, doc3KeywordFeature.size());
    Assertions.assertEquals("Jane Smith", doc3KeywordFeature.get("author").asText());
    Assertions.assertEquals("French", doc3KeywordFeature.get("language").asText());
  }

