      new TestDeletionCriteria("empty_tags", "nonexistent", null, null),
      new TestDeletionCriteria("single_tag", "only", null, null)
    );
    final String taskId = elasticsearchService.bulkRemovePropertyValues(
      index,
      criteria,
      documentIds
    );

    Assertions.assertNotNull(taskId);
    Assertions.assertDoesNotThrow(() ->
      elasticsearchService.waitForTaskCompletion(taskId, 10).join()
    );

    elasticsearchService.refresh(index);

    final SearchResponse<JsonNode> response = elasticsearchService.search(
      index,
      Query.of(b -> b.matchAll(m -> m)),
      JsonNode.class
    );

    final JsonNode doc = response.hits().hits().get(0).source();
    final JsonNode keywordFeature = doc.get("keyword_feature");

    // empty_tags should remain empty
    final JsonNode emptyTags = keywordFeature.get("empty_tags");
    Assertions.assertTrue(emptyTags.isArray());
    Assertions.assertEquals(0, emptyTags.size());

    // single_tag should be removed completely (became empty)
    Assertions.assertNull(keywordFeature.get("single_tag"));

    // category should remain
    Assertions.assertEquals("keep", keywordFeature.get("category").asText());
  }
}

