  private final String INDEX_SUFFIX = "-atlantis-" + CURRENT_TEMPLATE_VERSION;

  @PostConstruct
  void init() throws IOException {
    elasticsearchService.addIndexToIgnore(geoShapeService.getIndex());
    elasticsearchService.addIndexToIgnore(geocodingService.getIndex());
    setup();
    geoShapeService.init();
    geocodingService.init();
  }

  @PreDestroy
  void destroy() {
    elasticsearchService.removeIndexFromIgnore(geoShapeService.getIndex());
    elasticsearchService.removeIndexFromIgnore(geocodingService.getIndex());
    elasticsearchService.cleanup();
  }

