  @Test
  void testItCanUpdateASubsetByQuery()
    throws IOException, InterruptedException {
    final String pipeline =
      """
      {
        "processors": [
          {
            "script": {
              "source": "ctx.keyword_feature.name = 'updated'",
              "lang": "painless"
            }
          }
        ]
      }
      """;
    final String pipelineName = UUID.randomUUID().toString();
    elasticsearchService.putPipeline(
      pipelineName,
      new ByteArrayInputStream(pipeline.getBytes())
    );

    final String indexName = randomIndexName();
    elasticsearchService.createIndex(indexName);
    final List<JsonNode> data = List.of(
      objectMapper.readTree(
        "{\"keyword_feature\": {\"id\": \"1\", \"name\": \"object1\", \"description\": \"description1\"}}"
      ),
      objectMapper.readTree(
        "{\"keyword_feature\": {\"id\": \"2\", \"name\": \"object2\", \"description\": \"description2\"}}"
      )
    );
    elasticsearchService.insert(indexName, data);
    elasticsearchService.refresh(indexName);

    final String taskId = elasticsearchService.updateByQuery(
      indexName,
      "{\"match\": {\"keyword_feature.id\": \"1\"}}",
      pipelineName
    );
    Thread.sleep(1000); // Wait for the task to start
    Assertions.assertDoesNotThrow(() ->
      elasticsearchService.waitForTaskCompletion(taskId, 10).join()
    );

    // Ensure the documents were updated
    final SearchResponse<JsonNode> response = elasticsearchService.search(
      indexName,
      Query.of(b -> b.matchAll(m -> m)),
      JsonNode.class
    );
    // Assert that only the document with id 1 was updated
    final List<Hit<JsonNode>> hits = response.hits().hits();
    Assertions.assertEquals(2, hits.size());
    final JsonNode doc1 = hits.get(0).source();
    final JsonNode doc2 = hits.get(1).source();

    // Assert only one of the documents was updated
    if (doc1.at("/keyword_feature/id").asText().equals("1")) {
      Assertions.assertEquals(
        "updated",
        doc1.at("/keyword_feature/name").asText()
      );
      Assertions.assertTrue(
        doc2.at("/keyword_feature/name").asText().startsWith("object")
      );
    } else {
      Assertions.assertEquals(
        "updated",
        doc2.at("/keyword_feature/name").asText()
      );
      Assertions.assertTrue(
        doc1.at("/keyword_feature/name").asText().startsWith("object")
      );
    }
  }

