  @Test
  void testBulkRemovePropertyValues_MultipleDocumentsWithSameProperty()
    throws IOException, InterruptedException {
    final String index = randomIndexName();
    elasticsearchService.createIndex(index);

    // Create multiple test documents with same property to remove
    final List<JsonNode> testData = List.of(
      objectMapper.readTree(
        """
        {
          "title": "Document 1",
          "keyword_feature": {
            "status": "draft",
            "author": "John"
          }
        }
        """
      ),
      objectMapper.readTree(
        """
        {
          "title": "Document 2",
          "keyword_feature": {
            "status": "draft",
            "category": "tech"
          }
        }
        """
      ),
      objectMapper.readTree(
        """
        {
          "title": "Document 3",
          "keyword_feature": {
            "status": "published",
            "author": "Jane"
          }
        }
        """
      )
    );

    final List<String> documentIds = List.of("doc1", "doc2", "doc3");
    elasticsearchService.insert(index, testData, documentIds);
    elasticsearchService.refresh(index);

    // Remove "draft" status from all documents
    final List<Object> criteria = List.of(
      new TestDeletionCriteria("status", "draft", null, null)
    );
    final String taskId = elasticsearchService.bulkRemovePropertyValues(
      index,
      criteria,
      documentIds
    );

    Assertions.assertNotNull(taskId);

    Assertions.assertDoesNotThrow(() ->
      elasticsearchService.waitForTaskCompletion(taskId, 10).join()
    );

    elasticsearchService.refresh(index);

    // Verify results
    final SearchResponse<JsonNode> response = elasticsearchService.search(
      index,
      Query.of(b -> b.matchAll(m -> m)),
      JsonNode.class
    );

    Assertions.assertEquals(3L, response.hits().total().value());

    // Check that draft status was removed from doc1 and doc2, but published status remains in doc3
    for (Hit<JsonNode> hit : response.hits().hits()) {
      final JsonNode doc = hit.source();
      final JsonNode keywordFeature = doc.get("keyword_feature");

      if ("doc1".equals(hit.id())) {
        // Should only have author property now
        Assertions.assertEquals(1, keywordFeature.size());
        Assertions.assertEquals("John", keywordFeature.get("author").asText());
        Assertions.assertNull(keywordFeature.get("status"));
      } else if ("doc2".equals(hit.id())) {
        // Should only have category property now
        Assertions.assertEquals(1, keywordFeature.size());
        Assertions.assertEquals("tech", keywordFeature.get("category").asText());
        Assertions.assertNull(keywordFeature.get("status"));
      } else if ("doc3".equals(hit.id())) {
        // Should have both properties (published status was not removed)
        Assertions.assertEquals(2, keywordFeature.size());
        Assertions.assertEquals("published", keywordFeature.get("status").asText());
        Assertions.assertEquals("Jane", keywordFeature.get("author").asText());
      }
    }
  }

