  @Test
  void testBulkRemovePropertyValues_SamePropertyDifferentNamespaces()
    throws IOException, InterruptedException {
    final String index = randomIndexName();
    elasticsearchService.createIndex(index);

    final List<JsonNode> testData = List.of(
      objectMapper.readTree(
        """
        {
          "keyword_feature": {
            "value": ["keep", "remove", "keep2"]
          },
          "text_feature": {
            "value": "remove"
          },
          "numeric_feature": {
            "value": 42,
            "other": "keep"
          }
        }
        """
      )
    );

    final List<String> documentIds = List.of("doc1");
    elasticsearchService.insert(index, testData, documentIds);
    elasticsearchService.refresh(index);

    // Remove "remove" from value property in all namespaces
    final List<Object> criteria = List.of(
      new TestDeletionCriteria("value", "remove", null, null)
    );
    final String taskId = elasticsearchService.bulkRemovePropertyValues(
      index,
      criteria,
      documentIds
    );

    Assertions.assertNotNull(taskId);
    Assertions.assertDoesNotThrow(() ->
      elasticsearchService.waitForTaskCompletion(taskId, 10).join()
    );

    elasticsearchService.refresh(index);

    final SearchResponse<JsonNode> response = elasticsearchService.search(
      index,
      Query.of(b -> b.matchAll(m -> m)),
      JsonNode.class
    );

    final JsonNode doc = response.hits().hits().get(0).source();

    // keyword_feature: array should have "remove" removed, keep others
    final JsonNode keywordValues = doc.get("keyword_feature").get("value");
    Assertions.assertEquals(2, keywordValues.size());
    Assertions.assertEquals("keep", keywordValues.get(0).asText());
    Assertions.assertEquals("keep2", keywordValues.get(1).asText());

    // text_feature: single "remove" value should cause field removal
    Assertions.assertNull(doc.get("text_feature").get("value"));

    // numeric_feature: 42 != "remove" so should be unchanged
    Assertions.assertEquals(42, doc.get("numeric_feature").get("value").asInt());
    Assertions.assertEquals("keep", doc.get("numeric_feature").get("other").asText());
  }

