// Serve the user
// Handle the request and return the response
export default function API(request: Request) {
  const pathname = new URL(request.url).pathname;

  // Get a saved conversation
  // GET /api/conversation/:conversationId
  if (request.method === "GET" && pathname.startsWith("/api/conversation/")) {
    const conversationId = pathname.split("/").pop();

    try {
      // TODO: Replace with actual database query once implemented
      // This is a mockup response for now
      const mockConversation = {
        id: conversationId,
        title: "Sample Conversation",
        dates: [
          { date: "2025-03-05", votes: 3 },
          { date: "2025-03-07", votes: 1 },
          { date: "2025-03-10", votes: 5 }
        ],
        participants: ["Alice", "Bob", "Charlie"]
      };

      return new Response(JSON.stringify(mockConversation), {
        status: 200,
        headers: { "Content-Type": "application/json" }
      });
    } catch (error) {
      console.error("Error fetching conversation:", error);
      return new Response(JSON.stringify({ error: "Failed to fetch conversation" }), {
        status: 500,
        headers: { "Content-Type": "application/json" }
      });
    }
  }

  // Return a 404 for any other request
  return new Response("Not found", { status: 404 });
}

